// src/components/PredictionForm.js
import React, { useState } from 'react';

function PredictionForm({ game, onSubmitPrediction }) {
  const [prediction, setPrediction] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmitPrediction(prediction, game.id); // Pass prediction and game ID
    setPrediction(''); // Reset prediction field
  };

  return (
    <form onSubmit={handleSubmit}>
      <h3>Predict the outcome of the game:</h3>
      <label>
        Prediction (e.g., "Home Win" or "Away Win"):
        <input
          type="text"
          value={prediction}
          onChange={(e) => setPrediction(e.target.value)}
          required
        />
      </label>
      <button type="submit">Submit Prediction</button>
    </form>
  );
}

export default PredictionForm;
