// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import PredictionForm from '../components/PredictionForm';  // Adjust path if needed

function Home() {
  const [nbaGames, setNbaGames] = useState([]);
  const [userPredictions, setUserPredictions] = useState([]);

  // Mock data for games (replace with actual data later)
  useEffect(() => {
    const mockGames = [
      { id: 'game1', homeTeam: 'Lakers', awayTeam: 'Warriors', date: '2025-04-22' },
      { id: 'game2', homeTeam: 'Celtics', awayTeam: 'Heat', date: '2025-04-23' },
      { id: 'game3', homeTeam: 'Bulls', awayTeam: 'Knicks', date: '2025-04-24' },
    ];
    setNbaGames(mockGames);
  }, []);

  // Handle prediction submission
  const handlePredictionSubmit = (prediction, gameId) => {
    setUserPredictions([
      ...userPredictions,
      { gameId, prediction }
    ]);
  };

  return (
    <div>
      <h1>Upcoming NBA Games</h1>
      {nbaGames.map((game) => (
        <div key={game.id}>
          <h2>{game.homeTeam} vs {game.awayTeam}</h2>
          <p>{game.date}</p>
          <PredictionForm game={game} onSubmitPrediction={handlePredictionSubmit} />
        </div>
      ))}

      <h2>Your Predictions</h2>
      {userPredictions.length > 0 ? (
        userPredictions.map((prediction, index) => (
          <div key={index}>
            <p>Game ID: {prediction.gameId} - Prediction: {prediction.prediction}</p>
          </div>
        ))
      ) : (
        <p>No predictions made yet.</p>
      )}
    </div>
  );
}

export default Home;
