import React, { useEffect, useState } from 'react';
import PredictionForm from '../components/PredictionForm';  // Adjust the path if necessary

// Function to fetch NBA games
const fetchTennis = async () => {
  // Simulated data
  return [
    { playerA: 'Nadal', playerB: 'Djokovic', date: '2025-04-22' },
    { playerA: 'Swiatek', playerB: 'Gauff', date: '2025-04-23' },
  ];
};

const fetchNBA = async () => {
  return [
    { homeTeam: 'Lakers', awayTeam: 'Warriors', date: '2025-04-22' },
    { homeTeam: 'Celtics', awayTeam: 'Heat', date: '2025-04-23' },
  ];
};

const fetchVolleyball = async () => {
  return [
    { teamA: 'Brazil', teamB: 'Italy', date: '2025-04-24' },
    { teamA: 'USA', teamB: 'Russia', date: '2025-04-25' },
  ];
};


function Home() {
  const [username, setUsername] = useState('');
  const [nbaGames, setNbaGames] = useState([]);
  const [tennisGames, setTennisGames] = useState([]);
  const [volleyballGames, setVolleyballGames] = useState([]);

  // Fetch username from localStorage or redirect to login if not logged in
  useEffect(() => {
    const storedUsername = localStorage.getItem('username');
    if (storedUsername) {
      setUsername(storedUsername);
    } else {
      window.location.href = '/';
    }
  }, []);

  // Fetch games when the component mounts
  useEffect(() => {
    const fetchGames = async () => {
      const nbaData = await fetchNBA();
      const tennisData = await fetchTennis();
      const volleyballData = await fetchVolleyball();

      setNbaGames(nbaData);
      setTennisGames(tennisData);
      setVolleyballGames(volleyballData);
    };

    fetchGames();
  }, []);  // Empty array to run only once when the component mounts

  return (
    <div>
      <h1>Welcome, {username}!</h1>
      <p>Letâ€™s start predicting games!</p>

      <h2>Upcoming NBA Games</h2>
      {nbaGames.length > 0 ? (
        nbaGames.map((game, index) => (
          <div key={index}>
            <p>{game.homeTeam} vs {game.awayTeam}</p>
            <p>{game.date}</p>
          </div>
        ))
      ) : (
        <p>Loading NBA games...</p>
      )}

      <h2>Upcoming Tennis Games</h2>
      {tennisGames.length > 0 ? (
        tennisGames.map((game, index) => (
          <div key={index}>
            <p>{game.playerA} vs {game.playerB}</p>
            <p>{game.date}</p>
          </div>
        ))
      ) : (
        <p>Loading Tennis games...</p>
      )}

      <h2>Upcoming Volleyball Games</h2>
      {volleyballGames.length > 0 ? (
        volleyballGames.map((game, index) => (
          <div key={index}>
            <p>{game.teamA} vs {game.teamB}</p>
            <p>{game.date}</p>
          </div>
        ))
      ) : (
        <p>Loading Volleyball games...</p>
      )}

      <PredictionForm />  {/* Add the PredictionForm component here */}
    </div>
  );
}

export default Home;
