// src/components/PredictionForm.js
import React, { useState } from 'react';

function PredictionForm({ game, onSubmit }) {
  const [prediction, setPrediction] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!prediction) return alert('Please enter a prediction');
    onSubmit(game.id, prediction);
    setPrediction('');
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col gap-2">
      <label className="font-medium">
        {game.teams ? `${game.teams[0]} vs ${game.teams[1]}` : `${game.players[0]} vs ${game.players[1]}`}
      </label>
      <input
        type="text"
        value={prediction}
        onChange={(e) => setPrediction(e.target.value)}
        placeholder="Your prediction"
        className="p-2 border rounded"
      />
      <button
        type="submit"
        className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded"
      >
        Submit
      </button>
    </form>
  );
}

export default PredictionForm;
