import React, { useState } from 'react';
import Login from './pages/Login';
import Home from './pages/Home';

function App() {
  const [user, setUser] = useState(null);

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Header */}
      <header className="bg-white p-4 shadow flex items-center justify-between">
        <h1 className="text-2xl font-bold text-gray-800">
          üèÄüéæüèê Bry's Sports Prediction App
        </h1>
        {/* Move logo to the right */}
        <img
          src="/EZ P.png"
          alt="App Logo"
          className="h-10 w-auto ml-auto" // ml-auto will push it to the right
        />
      </header>

      {/* Main Content */}
      <main className="p-4">
        {user ? <Home user={user} /> : <Login onLogin={setUser} />}
      </main>
    </div>
  );
}

export default App;
